import React from "react";
import { withStore } from "@spyna/react-store";
import { Styles, withStyles } from "@material-ui/styles";
import classNames from "classnames";
import AddressValidator from "wallet-address-validator";
import bchaddr from "bchaddrjs";
import {
    addTx,
    updateTx,
    removeTx,
    gatherFeeData,
    MIN_TX_AMOUNTS,
} from "../utils/txUtils";
import {
    MINI_ICON_MAP,
    SYMBOL_MAP,
    NETWORK_MAP,
    NAME_MAP,
    abbreviateAddress,
    updateBalance,
} from "../utils/walletUtils";

import Grid from "@material-ui/core/Grid";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Typography from "@material-ui/core/Typography";
import ToggleButton from "@material-ui/lab/ToggleButton";
import ToggleButtonGroup from "@material-ui/lab/ToggleButtonGroup";
import CurrencySelect from "../components/CurrencySelect";
import BigCurrencyInput from "../components/BigCurrencyInput";
import ActionLink from "../components/ActionLink";
import DarkTooltip from "../components/DarkTooltip";

import theme from "../theme/theme";

import WalletIcon from "../assets/wallet-icon.svg";

const styles: Styles<typeof theme, any> = (theme) => ({
    container: {
        background: "#fff",
        border: "1px solid " + theme.palette.divider,
        borderRadius: 4,
        boxShadow: "0px 1px 2px rgba(0, 27, 58, 0.05)",
        maxWidth: 400,
        width: "100%",
        margin: "0px auto " + theme.spacing(1) + "px",

        [theme.breakpoints.down("sm")]: {
            maxWidth: "100%",
        },
    },
    transferActionTabs: {
        margin: "0px auto",
        // marginTop: theme.spacing(2),
        marginBottom: theme.spacing(1),
        "& div.MuiToggleButtonGroup-root": {
            width: "100%",
        },
        "& button": {
            width: "50%",
        },
    },
    depositAddressContainer: {},
    depositAddress: {
        width: "100%",
    },
    actionButtonContainer: {
        paddingTop: theme.spacing(2),
        paddingBottom: theme.spacing(2),
        textAlign: "center",
        "& button": {
            "&.Mui-disabled": {},
            margin: "0px auto",
            fontSize: 12,
            minWidth: 175,
            padding: theme.spacing(1),
        },
    },
    amountField: {
        width: "100%",
    },
    depositButton: {},
    withdrawButton: {},
    actions: {
        paddingTop: theme.spacing(1),
        padding: theme.spacing(3),
    },
    transactionsContainer: {
        padding: theme.spacing(3),
        paddingTop: theme.spacing(0),
        marginTop: theme.spacing(2),
        borderTop: "1px solid #EBEBEB",
    },
    actionsContainer: {
        borderRadius: theme.shape.borderRadius,
        borderTopLeftRadius: 0,
        borderTopRightRadius: 0,
    },
    destChooser: {
        width: "100%",
        marginTop: theme.spacing(2),
        marginBottom: theme.spacing(1),
        "& div.MuiToggleButtonGroup-root": {
            width: "100%",
        },
        "& button": {
            width: "50%",
        },
    },
    fees: {
        width: "100%",
        border: "1px solid " + theme.palette.divider,
        fontSize: 12,
        padding: theme.spacing(1),
        paddingBottom: 0,
        marginTop: theme.spacing(1),
        marginBottom: theme.spacing(3),
        display: "flex",
        flexDirection: "column",
        "& span": {
            marginBottom: theme.spacing(1),
        },
    },
    icon: {
        width: 16,
        height: 16,
        marginRight: theme.spacing(0.75),
    },
    toggle: {
        "& button": {
            minHeight: "auto",
            border: "1px solid transparent",
            borderBottom: "1px solid " + theme.palette.divider,
            height: 56,
            backgroundColor: "#fff",
            "&:first-child": {
                borderRight: "1px solid " + theme.palette.divider,
            },
            "&.MuiToggleButton-root": {},
            "&.Mui-selected": {
                borderBottom: "1px solid transparent",
                color: theme.palette.primary.main,
                backgroundColor: "#transparent !important",
            },
            "& .MuiToggleButton-label": {
                fontSize: 16,
            },
            "& span": {
                textTransform: "capitalize !important",
            },
        },
    },
    title: {
        paddingTop: theme.spacing(2),
        paddingBottom: theme.spacing(3),
    },
    optionsContainer: {
        border: "1px solid #EDEFF3",
        borderBottom: "none",
        borderRadius: 4,
        boxShadow: "0px 1px 2px rgba(0, 27, 58, 0.05)",
    },
    option: {
        borderBottom: "1px solid #EDEFF3",
        paddingLeft: theme.spacing(3),
        paddingRight: theme.spacing(3),
        minHeight: 60,
        fontSize: 14,
        "& img": {
            height: "auto",
            width: 24,
            marginRight: theme.spacing(1),
        },
        "& .MuiGrid-root": {
            display: "flex",
            alignItems: "center",
        },
    },
    standaloneOption: {
        border: "1px solid #DBE0E8",
        borderRadius: 4,
        marginBottom: theme.spacing(2),
        boxShadow: "0px 1px 2px rgba(0, 27, 58, 0.05)",
    },
    addressInput: {
        width: "100%",
        paddingTop: theme.spacing(2),
        paddingBottom: theme.spacing(2),
    },
    currencySelect: {
        marginLeft: theme.spacing(-1),
        width: "calc(100% + 8px)",
    },
    balanceContainer: {
        display: "flex",
        alignItems: "flex-end",
        marginBottom: theme.spacing(1),
    },
    amountContainer: {
        paddingTop: theme.spacing(5),
        paddingBottom: theme.spacing(5),
    },
    totalCell: {
        wordBreak: "break-word",
    },
    amountError: {
        textAlign: "center",
        color: "#FF4545",
        fontSize: 12,
        margin: "0px auto",
    },
    disclosure: {
        width: "100%",
        maxWidth: 370,
        marginLeft: "auto",
        marginRight: "auto",
        textAlign: "center",
        boxShadow: "0px 1px 2px rgba(0, 27, 58, 0.05)",
        padding: theme.spacing(2),
        color: theme.palette.primary.main,
        border: "1px solid " + theme.palette.primary.main,
        fontSize: 12,
        lineHeight: "17px",
        borderRadius: 4,
        marginBottom: theme.spacing(3),
        "& a": {
            color: "inherit",
        },
        [theme.breakpoints.down("sm")]: {
            maxWidth: "100%",
        },
    },
});

class TransferContainer extends React.Component<any> {
    burnInputRef = React.createRef();
    constructor(props: any) {
        super(props);
        this.state = props.store.getState();
    }

    showDepositModal(tx: any) {
        const { store } = this.props;
        store.set("showDepositModal", true);
        store.set("depositModalTx", tx);
    }

    async gatherFeeData() { }

    getBalance(asset: string) {
        const { store } = this.props;
        return store.get(`${asset.toUpperCase()}Balance`);
    }

    validateDeposit() {
        const { store } = this.props;
        const selectedAsset = store.get("selectedAsset");
        const amount = store.get("convert.amount");
        const amountValid =
            Number(amount) >=
            MIN_TX_AMOUNTS[selectedAsset as keyof typeof MIN_TX_AMOUNTS];

        if (!amount || !amountValid) {
            store.set("convert.showAmountError", true);
            return false;
        } else {
            store.set("convert.showAmountError", false);
            return true;
        }
    }

    validateWithdraw() {
        const { store } = this.props;
        const amount = store.get("convert.amount");
        const convertAddressValid = store.get("convert.destinationValid");
        const showAddressError = !convertAddressValid;
        const selectedAsset: keyof typeof MIN_TX_AMOUNTS = store.get(
            "selectedAsset"
        );
        const selectedFormat: keyof typeof SYMBOL_MAP = store.get(
            "convert.selectedFormat"
        );
        const balance = store.get(SYMBOL_MAP[selectedFormat] + "Balance");
        console.log('TRANSFER BALANCE', balance)
        const amountValid =
            Number(amount) >= MIN_TX_AMOUNTS[selectedAsset] &&
            amount <= Number(balance);

        if (showAddressError) {
            store.set("convert.showDestinationError", true);
        } else {
            store.set("convert.showDestinationError", false);
        }

        if (!amount || !amountValid) {
            store.set("convert.showAmountError", true);
        } else {
            store.set("convert.showAmountError", false);
        }

        if (showAddressError || !amount || !amountValid) {
            return false;
        } else {
            return true;
        }
    }

    async newDeposit() {
        const { store } = this.props;

        const amount = store.get("convert.amount");
        const localWeb3Address = store.get("localWeb3Address");
        const network = store.get("selectedNetwork");
        const format: keyof typeof NETWORK_MAP = store.get(
            "convert.selectedFormat"
        );
        const asset: keyof typeof NETWORK_MAP = store.get("selectedAsset");

        const tx = {
            id: "tx-" + Math.floor(Math.random() * 10 ** 16),
            type: "convert",
            instant: false,
            // awaiting: `${asset}-init`,
            sourceAsset: asset,
            sourceNetwork: NETWORK_MAP[asset],
            sourceNetworkVersion: network,
            destAddress: localWeb3Address,
            destNetwork: NETWORK_MAP[format],
            destNetworkVersion: network,
            destAsset: format,
            amount: Number(amount),
            error: false,
            txHash: "",
        };

        if (!this.validateDeposit()) {
            return;
        }

        store.set("confirmTx", tx);
        store.set("confirmAction", "deposit");
    }

    async newWithdraw() {
        const { store } = this.props;

        const amount = store.get("convert.amount");
        const destination = store.get("convert.destination");
        const network = store.get("selectedNetwork");
        const format: keyof typeof NETWORK_MAP = store.get(
            "convert.selectedFormat"
        );
        const asset: keyof typeof NETWORK_MAP = store.get("selectedAsset");

        const tx = {
            id: "tx-" + Math.floor(Math.random() * 10 ** 16),
            type: "convert",
            instant: false,
            sourceAsset: format,
            sourceNetwork: NETWORK_MAP[format],
            sourceNetworkVersion: network,
            destAddress: destination,
            destNetwork: NETWORK_MAP[asset],
            destNetworkVersion: network,
            destAsset: asset,
            amount: Number(amount),
            error: false,
            txHash: "",
        };

        if (!this.validateWithdraw()) {
            return;
        }

        store.set("confirmTx", tx);
        store.set("confirmAction", "withdraw");
    }

    render() {
        const { classes, store } = this.props;

        const selectedNetwork = store.get("selectedNetwork");
        const selectedTab = store.get("selectedTab");
        const selectedAsset: keyof typeof MIN_TX_AMOUNTS = store.get(
            "selectedAsset"
        );

        // 0 = mint, 1 = release
        const selectedDirection = store.get("convert.selectedDirection");
        const selectedFormat: keyof typeof SYMBOL_MAP = store.get(
            "convert.selectedFormat"
        );

        // console.log(selectedFormat);
        const localWeb3Address = store.get("localWeb3Address");
        // console.log(localWeb3Address);
        const balance = store.get(SYMBOL_MAP[selectedFormat] + "Balance");
        // console.log(balance);
        const amount = store.get("convert.amount");
        // console.log(amount);
        const total = store.get("convert.conversionTotal");

        const allowance = store.get("convert.adapterWbtcAllowance");
        const hasAllowance = Number(amount) <= Number(allowance);
        const allowanceRequesting = store.get(
            "convert.adapterWbtcAllowanceRequesting"
        );

        const convertAddressValid = store.get("convert.destinationValid");
        const canConvertTo = amount >= MIN_TX_AMOUNTS[selectedAsset];
        const canConvertFrom =
            Number(amount) >= MIN_TX_AMOUNTS[selectedAsset] &&
            amount <= Number(balance) &&
            convertAddressValid;
        const showAmountError = store.get("convert.showAmountError");
        const showDestinationError = store.get("convert.showDestinationError");
        const destAsset = selectedDirection ? selectedAsset : selectedFormat;
        // console.log(destAsset);

        const usdValue = Number(store.get(`${selectedAsset}usd`) * amount).toFixed(
            2
        );

        return (
            <React.Fragment>
                <Grid container>
                    <div className={classes.disclosure}>
                        <Typography variant="inherit">
                            RenVM is new technology and security audits don't completely
                            eliminate risks. Please don’t supply assets you can’t afford
                            to&nbsp;lose.
              <br />
                            <br />
                            <b>
                                If you are new to RenBridge, please watch{" "}
                                <a
                                    rel="noopener noreferrer"
                                    target="_blank"
                                    href="https://www.youtube.com/watch?v=kO0672RJL-Q&feature=youtu.be"
                                >
                                    this
                </a>{" "}
                                tutorial before&nbsp;continuing.
              </b>
                        </Typography>
                    </div>
                </Grid>
                <div className={classes.container}>
                    {
                        <Grid container className={classes.transferActionTabs}>
                            <ToggleButtonGroup
                                size="small"
                                className={classes.toggle}
                                value={String(selectedDirection)}
                                exclusive
                                onChange={(event, newValue) => {
                                    if (newValue) {
                                        store.set("convert.selectedDirection", Number(newValue));
                                        store.set("convert.amount", "");
                                        store.set("convert.networkFee", "");
                                        store.set("convert.conversionTotal", "");
                                        store.set("convert.destination", "");
                                        store.set("convert.showAmountError", false);
                                        store.set("convert.showDestinationError", false);
                                    }
                                }}
                            >
                                <ToggleButton disableRipple={true} key={0} value={"0"}>
                                    Mint
                </ToggleButton>
                                <ToggleButton disableRipple={true} key={1} value={"1"}>
                                    Release
                </ToggleButton>
                            </ToggleButtonGroup>
                        </Grid>
                    }
                    {selectedTab === 1 && (
                        <div className={classes.actionsContainer}>
                            <Grid className={classes.actions}>
                                <Grid container justify="center">
                                    <Grid item xs={12}>
                                        {selectedDirection === 0 && (
                                            <React.Fragment>
                                                <Grid className={classes.amountContainer} container>
                                                    <BigCurrencyInput
                                                        symbol={SYMBOL_MAP[selectedAsset]}
                                                        placeholder={"0.00 " + SYMBOL_MAP[selectedAsset]}
                                                        usdValue={usdValue}
                                                        value={amount}
                                                        onChange={(event: any) => {
                                                            const value = event.value || "";
                                                            store.set("convert.amount", String(value));
                                                            gatherFeeData();
                                                        }}
                                                    />
                                                    {showAmountError && (
                                                        <Typography className={classes.amountError}>
                                                            Minimum mint amount is{" "}
                                                            {MIN_TX_AMOUNTS[selectedAsset]}{" "}
                                                            {SYMBOL_MAP[selectedAsset]}
                                                        </Typography>
                                                    )}
                                                </Grid>
                                                <Grid
                                                    className={classes.balanceContainer}
                                                    container
                                                    justify="space-between"
                                                >
                                                    <Typography variant="caption">
                                                        {SYMBOL_MAP[selectedAsset]} Balance
                          </Typography>
                                                    <Typography>
                                                        <ActionLink
                                                            onClick={() => {
                                                                store.set("convert.amount", balance);
                                                            }}
                                                        >
                                                            {balance} {SYMBOL_MAP[selectedAsset]}
                                                        </ActionLink>
                                                    </Typography>
                                                </Grid>

                                                <Grid
                                                    container
                                                    className={classNames(
                                                        classes.standaloneOption,
                                                        classes.option
                                                    )}
                                                >
                                                    <Grid item xs={6}>
                                                        Asset
                          </Grid>
                                                    <Grid item xs={6}>
                                                        <CurrencySelect
                                                            active={SYMBOL_MAP[selectedAsset]}
                                                            className={classes.currencySelect}
                                                            items={["ETH"]}
                                                            onCurrencyChange={(v: string) => {
                                                                const asset = v.toLowerCase();
                                                                store.set(
                                                                    "convert.selectedFormat",
                                                                    `w${asset}`
                                                                );
                                                                store.set("selectedAsset", asset);
                                                                gatherFeeData();
                                                            }}
                                                        />
                                                    </Grid>
                                                </Grid>

                                                <Grid
                                                    className={classes.optionsContainer}
                                                    container
                                                    direction="column"
                                                >
                                                    <Grid container className={classes.option}>
                                                        <Grid item xs={6}>
                                                            Destination
                            </Grid>
                                                        <Grid item xs={6}>
                                                            <DarkTooltip
                                                                placement="top"
                                                                title={localWeb3Address}
                                                                arrow
                                                            >
                                                                <div>
                                                                    <img src={WalletIcon} />
                                                                    {abbreviateAddress(localWeb3Address)}
                                                                </div>
                                                            </DarkTooltip>
                                                        </Grid>
                                                    </Grid>
                                                    <Grid container className={classes.option}>
                                                        <Grid item xs={6}>
                                                            You will receive
                            </Grid>
                                                        <Grid item xs={6} className={classes.totalCell}>
                                                            <img src={MINI_ICON_MAP[destAsset]} />
                                                            {total || ""} {SYMBOL_MAP[destAsset]}
                                                        </Grid>
                                                    </Grid>
                                                </Grid>
                                            </React.Fragment>
                                        )}

                                        {selectedDirection === 1 && (
                                            <React.Fragment>
                                                <Grid className={classes.amountContainer} container>
                                                    <BigCurrencyInput
                                                        symbol={SYMBOL_MAP[selectedFormat]}
                                                        value={amount}
                                                        placeholder={"0.00 " + SYMBOL_MAP[selectedFormat]}
                                                        usdValue={usdValue}
                                                        onChange={(event: any) => {
                                                            const value = event.value || "";
                                                            store.set("convert.amount", String(value));
                                                            gatherFeeData();
                                                        }}
                                                    />
                                                    {showAmountError && (
                                                        <Typography className={classes.amountError}>
                                                            Please enter a valid amount to release
                            </Typography>
                                                    )}
                                                </Grid>
                                                <Grid
                                                    className={classes.balanceContainer}
                                                    container
                                                    justify="space-between"
                                                >
                                                    <Typography variant="caption">
                                                        {SYMBOL_MAP[selectedFormat]} Balance
                          </Typography>
                                                    <Typography>
                                                        <ActionLink
                                                            onClick={() => {
                                                                store.set("convert.amount", balance);
                                                            }}
                                                        >
                                                            {balance} {SYMBOL_MAP[selectedFormat]}
                                                        </ActionLink>
                                                    </Typography>
                                                </Grid>
                                                <Grid
                                                    container
                                                    className={classNames(
                                                        classes.standaloneOption,
                                                        classes.option
                                                    )}
                                                >
                                                    <Grid item xs={6}>
                                                        Asset
                          </Grid>
                                                    <Grid item xs={6} onClick={updateBalance}>
                                                        <CurrencySelect
                                                            active={SYMBOL_MAP[selectedFormat]}
                                                            className={classes.currencySelect}
                                                            items={["renBTC", "renZEC", "renBCH"]}
                                                            // items={["ETH"]}
                                                            renBTCBalance={store.get("renBTCBalance")}
                                                            renZECBalance={store.get("renZECBalance")}
                                                            renBCHBalance={store.get("renBCHBalance")}
                                                            // ETHBalance={store.get("ethbal")}
                                                            onCurrencyChange={(v: string) => {
                                                                const asset = v.toLowerCase();
                                                                store.set("convert.selectedFormat", asset);
                                                                store.set(
                                                                    "selectedAsset",
                                                                    asset.replace("ren", "")
                                                                );
                                                                gatherFeeData();
                                                            }}
                                                        />
                                                    </Grid>
                                                </Grid>
                                                <Grid
                                                    container
                                                    className={classNames(
                                                        classes.standaloneOption,
                                                        classes.option
                                                    )}
                                                >
                                                    <Grid xs={12}>
                                                        <div className={classes.addressInput}>
                                                            <TextField
                                                                label="Destination"
                                                                placeholder={`Enter ${NAME_MAP[selectedAsset]} Address`}
                                                                size="medium"
                                                                fullWidth={true}
                                                                error={showDestinationError}
                                                                helperText={
                                                                    showDestinationError
                                                                        ? `Please enter a valid ${NAME_MAP[selectedAsset]} address`
                                                                        : ""
                                                                }
                                                                InputProps={{
                                                                    disableUnderline: true,
                                                                }}
                                                                InputLabelProps={{
                                                                    shrink: true,
                                                                }}
                                                                inputProps={{
                                                                    onFocus: () => {
                                                                        store.set(
                                                                            "convert.destinationInputFocused",
                                                                            true
                                                                        );
                                                                    },
                                                                    onBlur: () => {
                                                                        store.set(
                                                                            "convert.destinationInputFocused",
                                                                            false
                                                                        );
                                                                    },
                                                                }}
                                                                onChange={(event) => {
                                                                    const value = event.target.value;
                                                                    store.set("convert.destination", value);
                                                                    store.set(
                                                                        "convert.destinationValid",
                                                                        AddressValidator.validate(
                                                                            value,
                                                                            selectedAsset.toUpperCase(),
                                                                            selectedNetwork === "testnet"
                                                                                ? "testnet"
                                                                                : "prod"
                                                                        )
                                                                    );
                                                                }}
                                                            />
                                                        </div>
                                                    </Grid>
                                                </Grid>

                                                <Grid
                                                    className={classes.optionsContainer}
                                                    container
                                                    direction="column"
                                                >
                                                    <Grid container className={classes.option}>
                                                        <Grid item xs={6}>
                                                            You will receive
                            </Grid>
                                                        <Grid item xs={6} className={classes.totalCell}>
                                                            <img src={MINI_ICON_MAP[destAsset]} />
                                                            {total || ""} {SYMBOL_MAP[destAsset]}
                                                        </Grid>
                                                    </Grid>
                                                </Grid>
                                            </React.Fragment>
                                        )}
                                    </Grid>
                                </Grid>

                                <Grid
                                    container
                                    justify="center"
                                    className={classes.actionButtonContainer}
                                >
                                    {selectedDirection === 0 && (
                                        <Grid item xs={12}>
                                            <Button
                                                disabled={!total || parseFloat(total) < 0.0000001}
                                                variant={"contained"}
                                                disableRipple
                                                color="primary"
                                                size="large"
                                                fullWidth
                                                className={classNames(
                                                    classes.margin,
                                                    classes.actionButton
                                                )}
                                                onClick={this.newDeposit.bind(this)}
                                            >
                                                Next
                      </Button>
                                        </Grid>
                                    )}
                                    {selectedDirection === 1 && (
                                        <Grid item xs={12}>
                                            <Button
                                                disabled={!total || parseFloat(total) < 0.0000001}
                                                variant={"contained"}
                                                disableRipple
                                                color="primary"
                                                fullWidth
                                                size="large"
                                                className={classNames(
                                                    classes.margin,
                                                    classes.actionButton
                                                )}
                                                onClick={this.newWithdraw.bind(this)}
                                            >
                                                Next
                      </Button>
                                        </Grid>
                                    )}
                                </Grid>
                            </Grid>
                        </div>
                    )}
                </div>
            </React.Fragment>
        );
    }
}

export default withStyles(styles)(withStore(TransferContainer));
