import { strip0x } from "./hex";
export const createAddress = (networks, opcode, script) => (isTestnet, mpkh, gHash) => {
    return new script()
        .add(Buffer.from(strip0x(gHash), "hex"))
        // .add(mintToAddress) // Is this meant to be here?
        .add(opcode.OP_DROP)
        .add(opcode.OP_DUP)
        .add(opcode.OP_HASH160)
        .add(Buffer.from(strip0x(mpkh), "hex"))
        .add(opcode.OP_EQUALVERIFY)
        .add(opcode.OP_CHECKSIG)
        .toScriptHashOut().toAddress(isTestnet ? networks.testnet : networks.mainnet).toString();
};
