export const validateString = (shouldBeString, errorMessage, options) => {
    if (typeof shouldBeString !== "string") {
        throw new Error(errorMessage);
    }
    if (options && options.indexOf(shouldBeString) === -1) {
        throw new Error(errorMessage);
    }
    return true;
};
