import React from "react";
import theme from "../theme/theme";
import { withStyles } from "@material-ui/styles";
import classNames from "classnames";
import Grid from "@material-ui/core/Grid";
import MenuItem from "@material-ui/core/MenuItem";
import Button from "@material-ui/core/Button";
import Menu from "@material-ui/core/Menu";
import ArrowDropDown from "@material-ui/icons/ArrowDropDown";

import {
  BRIDGE_SYMBOL_MAP,
  BRIDGE_NAME_MAP,
  BRIDGE_ICON_MAP,
} from "../utils/walletUtils";

const NoCapsButton = withStyles({
  root: {
    textTransform: "none",
  },
})(Button);

const styles = () => ({
  amountField: {
    width: "100%",
  },
  endAdornment: {
    "& p": {},
  },
  item: {
    display: "flex",
    fontSize: 14,
    alignItems: "center",
    minWidth: 55,
    paddingLeft: theme.spacing(2),
    paddingRight: theme.spacing(2),
    paddingTop: theme.spacing(1),
    paddingBottom: theme.spacing(1),
    "& div": {
      display: "flex",
    },
    justifyContent: "flex-start",
  },
  select: {
    display: "flex",
    "& div": {
      display: "flex",
    },
    "& MuiInput-underline:before": {
      display: "none",
    },
  },
  icon: {
    width: 32,
    height: 32,
    marginRight: 10,
  },
  button: {
    fontSize: 14,
    color: "#585861",
    display: "flex",
    justifyContent: "flex-start",
  },
  arrow: {
    flex: 1,
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-end",
    "& svg": {
      width: 22,
      height: "auto",
      marginLeft: theme.spacing(0.75),
    },
  },
  balance: {
    fontSize: 12,
    marginTop: -2,
    color: "#585861",
  },
  menu: {},
  standaloneOption: {
    border: "1px solid #DBE0E8",
    borderRadius: 4,
    boxShadow: "0px 1px 2px rgba(0, 27, 58, 0.05)",
  },
  option: {
    borderBottom: "1px solid #EDEFF3",
    paddingLeft: theme.spacing(3),
    paddingRight: theme.spacing(3),
    minHeight: 60,
    fontSize: 14,
    "& img": {
      height: "auto",
      width: 24,
      marginRight: theme.spacing(1),
    },
    "& .MuiGrid-root": {
      display: "flex",
      alignItems: "center",
    },
  },
});

type Balances = {
  [key in string]: string | any;
};

interface Props extends Balances {
  items: Array<keyof typeof BRIDGE_SYMBOL_MAP>;
  classes: { [key in string]: string };
  active?: string;
  pair?: string;
  disabled?: boolean;
}

class BridgeSelect extends React.Component<Props> {
  bridgeEl: React.RefObject<any>;
  pairsEl: React.RefObject<any>;
  state = {
    currency: "",
    bridgeOpen: false,
    pairsOpen: false,
  };
  constructor(props: Props) {
    super(props);
    this.state = {
      currency: "",
      bridgeOpen: false,
      pairsOpen: false,
    };
    this.bridgeEl = React.createRef();
    this.pairsEl = React.createRef();
  }

  handleBridgeOpen() {
    this.setState({
      bridgeOpen: true,
    });
  }

  handleBridgeClose(event: any) {
    // console.log(event, event.target, event.target.value)
    const value = event.target.value;
    if (value) {
      this.props.onBridgeChange(value);
      this.setState({ currency: value });
    }
    this.setState({ bridgeOpen: false });
  }

  handlePairsOpen() {
    this.setState({
      pairsOpen: true,
    });
  }

  handlePairsClose(event: any) {
    // console.log(event, event.target, event.target.value)
    const value = event.target.value;
    if (value) {
      this.props.onBridgeChange(value);
      this.setState({ currency: value });
    }
    this.setState({ pairsOpen: false });
  }

  render() {
    const { classes, items, className, active, pair } = this.props;

    const { bridgeOpen, pairsOpen } = this.state;
    const selected = active || items[0];
    const chainPair = pair || items[0];

    console.log("ITEMS", items);

    let pairOptions = [...items].filter((item) => item !== selected);
    console.log("PAIR OPTIONS", pairOptions);

    return (
      <div className={className || ""}>
        <React.Fragment>
          <Grid
            container
            className={classNames(classes.standaloneOption, classes.option)}
          >
            <Grid item xs={6}>
              {/* Bridge Chain */}
              <NoCapsButton
                fullWidth
                className={classes.button}
                ref={this.bridgeEl}
                aria-controls="bridgeMenu"
                aria-haspopup="true"
                onClick={this.handleBridgeOpen.bind(this)}
              >
                <img
                  src={BRIDGE_ICON_MAP[selected.toLowerCase()]}
                  alt={selected}
                  className={classes.icon}
                />
                <span className={classes.assetSymbol}>
                  {BRIDGE_NAME_MAP[selected.toLowerCase()]}
                </span>
                <div className={classes.arrow}>
                  <ArrowDropDown />
                </div>
              </NoCapsButton>
              <Menu
                id="bridgeMenu"
                anchorEl={this.bridgeEl.current}
                keepMounted
                className={classes.menu}
                open={bridgeOpen}
                onClose={this.handleBridgeClose.bind(this)}
              >
                {items.map((i, index) => {
                  return (
                    <MenuItem
                      className={classes.item}
                      onClick={() => {
                        this.handleBridgeClose.bind(this)({
                          target: {
                            value: i,
                          },
                        });
                      }}
                      key={index}
                      value={i}
                    >
                      <div>
                        <img
                          src={BRIDGE_ICON_MAP[i.toLowerCase()]}
                          alt={i}
                          className={classes.icon}
                        />
                      </div>
                      <Grid
                        container
                        direction="column"
                        alignItems="flex-start"
                      >
                        <span>{i}</span>
                        <span className={classes.balance}>
                          {
                            BRIDGE_NAME_MAP[
                              i.toLowerCase() as keyof typeof BRIDGE_NAME_MAP
                            ]
                          }
                        </span>
                      </Grid>
                    </MenuItem>
                  );
                })}
              </Menu>
            </Grid>
            <Grid item xs={6}>
              {/* Pair Chain */}
              <NoCapsButton
                fullWidth
                className={classes.button}
                ref={this.pairsEl}
                aria-controls="pairsMenu"
                aria-haspopup="true"
                onClick={this.handlePairsOpen.bind(this)}
              >
                <img
                  src={BRIDGE_ICON_MAP[chainPair.toLowerCase()]}
                  alt={selected}
                  className={classes.icon}
                />
                <span className={classes.assetSymbol}>
                  {BRIDGE_NAME_MAP[chainPair.toLowerCase()]}
                </span>
                <div className={classes.arrow}>
                  <ArrowDropDown />
                </div>
              </NoCapsButton>
              <Menu
                id="pairsMenu"
                anchorEl={this.pairsEl.current}
                keepMounted
                className={classes.menu}
                open={pairsOpen}
                onClose={this.handlePairsClose.bind(this)}
              >
                {pairOptions.map((i, index) => {
                  return (
                    <MenuItem
                      className={classes.item}
                      onClick={() => {
                        this.handlePairsClose.bind(this)({
                          target: {
                            value: i,
                          },
                        });
                      }}
                      key={index}
                      value={i}
                    >
                      <div>
                        <img
                          src={BRIDGE_ICON_MAP[i.toLowerCase()]}
                          alt={i}
                          className={classes.icon}
                        />
                      </div>
                      <Grid
                        container
                        direction="column"
                        alignItems="flex-start"
                      >
                        <span>{i}</span>
                        <span className={classes.balance}>
                          {
                            BRIDGE_NAME_MAP[
                              i.toLowerCase() as keyof typeof BRIDGE_NAME_MAP
                            ]
                          }
                        </span>
                      </Grid>
                    </MenuItem>
                  );
                })}
              </Menu>
            </Grid>
          </Grid>
        </React.Fragment>
      </div>
    );
  }
}

export default withStyles(styles)(BridgeSelect);
