import { getStore } from "../services/storeService";

// Bridge selection options
export const BRIDGE_SYMBOL_MAP: { [key in string]: string } = {
    ela: "ELA",
    eth: "ETH",
    trx: "TRX"
}
export const BRIDGE_NAME_MAP: { [key in string]: string } = {
    ela: "Elastos",
    eth: "Ethereum",
    trx: "Tron"
}
export const BRIDGE_ICON_MAP: { [key in string]: string } = {
    ela: ELA,
    eth: ETH,
    trx: TRX
}

export const setBridge = async function(bridge: any, pair: any) {
    const store = getStore();
    store.set("selectedBridge", bridge);
    store.set("selectedPair", pair);
}
