import React from "react";
import Numeral from "numeral";
import NumberFormat from "react-number-format";
import classNames from "classnames";
import { Styles, withStyles } from "@material-ui/styles";

const styles: Styles<any, any> = () => ({
    container: {
        width: "100%",
        textAlign: "center",
        "& input": {
            fontFamily: "inherit",
            color: "#3F3F48",
        },
    },
    large: {
        "& input": {
            fontSize: 52,
        },
    },
    medium: {
        "& input": {
            fontSize: 42,
        },
    },
    small: {
        "& input": {
            fontSize: 32,
        },
    },
    smallest: {
        "& input": {
            fontSize: 22,
        },
    },
    input: {
        fontSize: 52,
        width: "100%",
        outline: "none",
        textAlign: "center",
        border: "0px solid transparent",
    },
    grayText: {
        color: "#D0D2D9",
    },
});

class BridgeSelect extends React.PureComponent<any> {
    ref: React.RefObject<any>;
    input: any;

    constructor(props: any) {
        super(props);
        this.ref = React.createRef();
        this.input = null;
    }

    componentDidMount() {
        const inputRef = this.props.inputRef;
        if (this.props.inputRef) {
            this.ref = inputRef;
        }

        if (this.input) {
            this.input.focus();
        }
    }

    render() {
        console.log('Bridge Select Component', this.props)
        const {
            classes,
            className,
            onChange,
            symbol,
            usdValue,
            value,
            placeholder,
        } = this.props;




        return (
            <div className={classNames(classes.container)}>
            </div>
        );
    }
}

export default withStyles(styles)(BridgeSelect);
